// this is where we make our request to the backend
import { GET_ITEMS, ADD_ITEM, DEL_ITEM } from './types';

export const getItems = () => {
    return {
        type: GET_ITEMS
    };
};

export const deleteItem = () => {
    return {
        type: DEL_ITEM
    };
};
